var mongoose = require('mongoose');
var moment = require('moment');

class CategoryService {
  constructor(connection) {
    this.connection = connection;
    this.CategorySchema = new mongoose.Schema({
      name: String,
      postcnt: {type: Number, default: 0},
      regdhm: {type: String, default: moment().format('YYYY-MM-DD HH:mm:ss')}
    })
    this.Category = mongoose.model('categorys', this.CategorySchema);
  }
  getCategorys(){
    return this.Category.find();
  }
  insertCategory(category){
    var newCategory = new this.Category(category);
    newCategory.save().then(() => {
      console.log('1 Category inserted.');
    })
  }
  deleteCategory(category_id){
    this.Category({_id: category_id}, (err, output) => {
      console.log('id : ', category_id, ' category deleted.');
    })
  }
}

module.exports = CategoryService;
