var mongoose = require('mongoose');
var moment = require('moment');

class PostService {
  constructor(){
    this.PostSchema = new mongoose.Schema({
      category: String,
      title: String,
      content: String,
      writer: String,
      regdhm: {type: String, default: moment().format('YYYY-MM-DD HH:mm:ss')},    //or Date
      viewcnt: Number,
      comments: [{
        id: Number,
        content: String,
        writer: String,
        regdhm: {type: String, default: moment().format('YYYY-MM-DD HH:mm:ss')},
        hasChild: Boolean,
        parent: Number
      }]
    });
    this.Post = mongoose.model('post', this.PostSchema);
  }

  getPosts(){
      return this.Post.find()
  }

  insertPost(post){
    var newPost = new this.Post(post);
    return newPost.save()
  }
  updatePost(post_id, post){
    return new Promise(resolve => {
      this.Post.update({_id: post_id}, {$set: post}, (err, ouput) => {
        resolve(err, output);
      })

    })
  }
  deletePost(post_id){
    this.Post.delete({_id: post_id}, (err, ouput) => {
      console.log('id : ', post_id, ' post deleted.');
    })
  }
}

module.exports = PostService;

